import { useState } from 'react'
import { Avatar } from '@mui/material'
import { styled, useTheme } from '@mui/material/styles'
import Image from 'next/image'
import Box from '@mui/material/Box'
import Drawer from '@mui/material/Drawer'
import CssBaseline from '@mui/material/CssBaseline'
import MuiAppBar, { AppBarProps as MuiAppBarProps } from '@mui/material/AppBar'
import Toolbar from '@mui/material/Toolbar'
import List from '@mui/material/List'
import IconButton from '@mui/material/IconButton'
import MenuIcon from '@mui/icons-material/Menu'
import ListItem from '@mui/material/ListItem'
import ListItemIcon from '@mui/material/ListItemIcon'
import Button from '@mui/material/Button'
import Menu from '@mui/material/Menu'
import MenuItem from '@mui/material/MenuItem'
import NestedMenu from './NestedMenu'
import HomeIcon from '@mui/icons-material/Home'
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar'
import SettingsIcon from '@mui/icons-material/Settings'
import { Typography } from '@mui/material'
import { Copyright } from '@mui/icons-material'

/* const Root = styled('div')(({ theme }: any) => ({
  [theme.breakpoints.up('md')]: {
    display: 'none',
  },
})) */

const drawerWidth = 240

const Main = styled('main', { shouldForwardProp: (prop: any) => prop !== 'open' })<{
  open?: boolean
}>(({ theme, open }: any) => ({
  flexGrow: 1,
  padding: theme.spacing(2),
  transition: theme.transitions.create('margin', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: `-${drawerWidth}px`,
  ...(open && {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),

    marginLeft: 0,
  }),
}))

interface AppBarProps extends MuiAppBarProps {
  open?: boolean
}
const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop: any) => prop !== 'open',
})<AppBarProps>(({ theme, open }: any) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}))

const DrawerHeader = styled('div')(({ theme }: any) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}))
export default function PersistentDrawerLeft({ children }: any) {
  const theme = useTheme()
  const [open, setOpen] = useState(false)
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null)
  const isOpen = Boolean(anchorEl)
  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget)
  }
  const handleClose = () => {
    setAnchorEl(null)
  }
  const handleDrawerOpen = () => {
    setOpen(true)
  }
  const handleDrawerClose = () => {
    setOpen(false)
  }
  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" open={open} style={{ background: 'white' }}>
        <Toolbar
          sx={{
            justifyContent: 'space-between',
            display: 'flex',
          }}>
          <Box display="flex" alignItems={'center'}>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              onClick={handleDrawerOpen}
              edge="start"
              sx={{ mr: 2, ...(open && { display: 'none' }) }}>
              <MenuIcon fontSize="large" color="action" />
            </IconButton>
            <img
              src="http://blog.podkrepi.bg/content/images/2021/09/podkrepi-bg-logo.svg"
              alt=""
              style={{ width: '170px', display: open ? 'none' : '' }}
            />
          </Box>
          <div>
            <Avatar
              onClick={handleClick}
              sx={{
                display: { lg: 'flex', md: 'flex', sm: 'flex', xs: 'none' },
                cursor: 'pointer',
              }}
              src="/broken-image.jpg"
            />
            <Menu
              id="demo-positioned-menu"
              aria-labelledby="demo-positioned-button"
              anchorEl={anchorEl}
              open={isOpen}
              onClose={handleClose}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}>
              <MenuItem
                onClick={() => {
                  console.log('profile')
                }}>
                Profile
              </MenuItem>
              <MenuItem
                onClick={() => {
                  console.log('account')
                }}>
                My account
              </MenuItem>
              <MenuItem
                onClick={() => {
                  console.log('logout')
                }}>
                Logout
              </MenuItem>
            </Menu>
          </div>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: { lg: drawerWidth, md: drawerWidth, sm: drawerWidth, xs: '100%' },
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}>
        <DrawerHeader
          sx={{
            position: 'relative',
            justifyContent: 'space-between',
            padding: '10px',
            marginBottom: '10px',
          }}>
          <Box
            sx={{
              display: { xs: 'flex', sm: 'none', md: 'none' },
              position: 'absolute',
              width: '100%',
              justifyContent: 'center',
              marginTop: '100px',
            }}>
            <Image
              src="data:image/png;base64,"
              width={150}
              height={150}
            />
          </Box>
          <Box display="flex" alignItems="center" justifyContent="start" width="100%">
            <IconButton sx={{ padding: { xs: '10px', sm: 0 } }} onClick={handleDrawerClose}>
              <MenuIcon fontSize="medium" color="action" />
            </IconButton>
            <Box sx={{ display: { sm: 'flex', xs: 'none' } }}>
              <img
                src="http://blog.podkrepi.bg/content/images/2021/09/podkrepi-bg-logo.svg"
                alt=""
                style={{ width: '150px', marginLeft: '20px' }}
              />
            </Box>
          </Box>
        </DrawerHeader>
        <List
          sx={{
            marginTop: { xs: '100px', sm: '-20px' },
            display: 'flex',
            flexDirection: 'column',
          }}>
          <NestedMenu />
          {['Начало', 'Коли', 'Настройки'].map((text, index) => (
            <ListItem
              button
              key={text}
              sx={{
                display: 'flex',
                justifyContent: { xs: 'center', sm: 'left' },
                padding: '10px',
              }}>
              <ListItemIcon sx={{ display: { xs: 'none', sm: 'flex' } }}>
                {text == 'Начало' ? (
                  <HomeIcon />
                ) : text === 'Коли' ? (
                  <DirectionsCarIcon />
                ) : (
                  <SettingsIcon />
                )}
              </ListItemIcon>
              <Button sx={{ display: { xs: 'none', sm: 'block' } }}>{text}</Button>
              <Button
                sx={{ display: { xs: 'block', sm: 'none' }, width: '96%' }}
                variant="outlined">
                {text}
              </Button>
            </ListItem>
          ))}
        </List>
      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        {children}
      </Main>
      <Box sx={{ position: 'absolute', width: '100%', bottom: '0', left: '0', right: '0' }}>
        <div
          style={{
            background: '#294e85',
            height: '70px',
            width: '100%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}>
          <Typography variant="h6" color="white" display="flex" alignItems="center">
            <Copyright />
            Copyright
          </Typography>
        </div>
      </Box>
    </Box>
  )
}
